import React, { useState } from "react";
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBIcon,
  MDBBadge,
  MDBCardFooter,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBBtn,
  MDBTypography,
} from "mdb-react-ui-kit";
import ProfilePage from "./Profilepage";

// class List extends Component {
//   constructor(props) {
//     super(props);

//   }
//   render() {
//     return (
//       <div>

//       </div>
//     );
//   }
// }

// export default List;

const List = () => {
  const [selectedSP, setSelectedSP] = useState(0);
  const [showProfile, setShowProfile] = useState(false);
  const [data, setdata] = useState([
    {
      id: "0",
      name: "John Doe",
      location: "Nairobi",
      about: "Way Down,We go",
      skill: "Masseuse",
      availability: "Available",
      image: "image",
    },
  ]);
  useEffect(() => {
    $.ajax({
      url: `/profile`,
      type: "GET",
      dataType: "json",
      success: (result) => {
        setdata(result.service);
        return;
      },
      error: (error) => {
        alert("Unable to load data. Please try your request again");
        return;
      },
    });
  }, []);
  const theeSelected = data[selectedSP - 1];

  const createPagination = () => {
    console.log("mia amor");
    let pageNumbers = [];
    let maxPage = Math.ceil(this.state.totalQuestions / 10);
    for (let i = 1; i <= maxPage; i++) {
      pageNumbers.push(
        <>
          <span
            key={i}
            className={`page-num ${i === this.state.page ? "active" : ""}`}
            onClick={() => {
              this.selectPage(i);
            }}
          >
            {i}
          </span>
          <h1>Holla commo</h1>
        </>
      );
    }
    return pageNumbers;
  };
  const SProvider = data.map((sp) => (
    <MDBCol xl={6} className="mb-4" key={sp.id}>
      <MDBCard>
        <MDBCardBody
          onClick={() => {
            setSelectedSP(sp.id);
            setShowProfile(true);
          }}
        >
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <img
                src={
                  sp.image_link
                    ? `profile_pics/${sp.image_link}`
                    : "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTPwial6oKnqe0VTFem0n2gERixQWcuxgzhdg&usqp=CAU}"
                }
                alt=""
                style={{ width: "45px", height: "45px" }}
                className="rounded-circle"
              />
              <div className="ms-3">
                <p className="fw-bold mb-1">{sp.name}</p>
                <p className="text-muted mb-0">{sp.email}</p>
              </div>
            </div>
            <MDBBadge pill color="success" light>
              {sp.availability}
            </MDBBadge>
          </div>
        </MDBCardBody>
        <MDBCardFooter
          background="light"
          border="0"
          className="p-2 d-flex justify-content-around"
        >
          <MDBBtn
            color="link"
            rippleColor="primary"
            className="text-reset m-0"
            a
            href="sms:+255753782444"
          >
            Message <MDBIcon fas icon="envelope" />
          </MDBBtn>
          <MDBBtn
            color="link"
            rippleColor="primary"
            className="text-reset m-0"
            a
            href="tel:+255753782444"
          >
            Call <MDBIcon fas icon="phone" />
          </MDBBtn>
        </MDBCardFooter>
      </MDBCard>
    </MDBCol>
  ));
  // const showWhat = showProfile ? (
  //   <MDBRow>{SProvider}</MDBRow>
  // ) : (
  //   <ProfilePage selectedSP={4} />
  // );

  if (!showProfile) {
    return (
      <>
        <MDBRow>{SProvider}</MDBRow>
        <div className="pagination-menu">{createPagination}</div>
        <h1>Hallo</h1>
      </>
    );
  }
  return (
    <>
      <ProfilePage theeSelected={theeSelected} />
    </>
  );
};

export default List;
